mapper.select:
SELECT 
FROM sys_user


        AND company_id = #{companyId}


        AND company_role = #{companyRole}


        AND user_type = #{userType}


        AND username LIKE CONCAT('%', #{username}, '%')


        AND phone_number LIKE CONCAT('%', #{phoneNumber}, '%')


        AND status = #{status}


ORDER BY id DESC

SELECT 
FROM sys_user

    AND company_id = #{companyId}

        AND username LIKE CONCAT('%', #{username}, '%')


        AND company_role = #{companyRole}


        AND status = #{status}


ORDER BY id DESC

mapper.update:
UPDATE sys_user
password = #{password}, nickname = #{nickname}, phone_number = #{phoneNumber}, email = #{email}, gender = #{gender}, user_type = #{userType}, company_id = #{companyId}, company_role = #{companyRole}, status = #{status}, updated_at = NOW()
WHERE id = #{id}
